import{ Tabs, Tab, Grid, Cell, Card, CardTitle, CardActions, CardMenu, Button, IconButton, CardText } from "react-mdl"
import React, { Component } from 'react';



class Projects extends Component {
    constructor(props) {
        super(props);
        this.state = {  activeTab: 0};
    }
    
     toggleCategories() {            
     if(this.state.activeTab === 0){
        return(  
               <Card shadow={5} style={{minWidth:'450', margin:'auto'}}>
               <CardTitle style = {{color: '#fff', height: '176px', background: 
               'url(http://blog.addthiscdn.com/wp-content/uploads/2014/11/addthis-react-flux-javascript-scaling.png) center / cover'}}>React Project #1 </CardTitle>
          <CardText>
            Lorem Ipsum is simply dummy text of the printing and typesetting industry.
          </CardText>
          <CardActions border>
              <Button colored>Github</Button>
            <Button colored>CodePen</Button>
              <Button colored>LiveDemo</Button>
          </CardActions>
            <CardMenu syle={{color: '#fff'}}>
               <IconButton name="share"/> 
            </CardMenu> 
        </Card> 
        
     
        )  
      }  else if(this.state.activeTab === 1) {
      return(
        <div><h1>This is Steam</h1></div>
      )
    }else if (this.state.activeTab === 2) {
        return(
            <div><h1>This is Spotify</h1></div>
        )
     }else if (this.state.activeTab === 3) {
        return(  
            <div><h1>This is Instagram</h1></div>  
        )   
      }   
}

    
    render() {
        return(
            <div clasName="category-tabs">
            <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId})} ripple>
            <Tab> React</Tab>
            <Tab>Steam</Tab>
            <Tab>Spotify</Tab>
            <Tab>Instagram</Tab>
              </Tabs>

              <section classname="projects-grid">
              <Grid className="project-grid">
                <Cell col={12}>
                    <div className="content"></div>
                </Cell>
              </Grid>
               {this.toggleCategories()}
            </section>
            </div>
            
            
        )
    
    }
}

export default Projects;
